/* eslint-disable jsx-a11y/img-redundant-alt */
import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
function Kamkala() {
  const [imgname, setImgname] = useState([]);
  const [url, setUrl] = useState({});
  const [foldername, setfoldername] = useState("");

  console.log(imgname, "imgname");
console.log(url)
  const apicheck = async () => {
    try {
      const result = await axios.get("http://192.168.29.3:8001/getFiles");

      if (result.data.status) {
        let imgName = [];
        let fildernameurl = [];
        for (let i = 0; i < result.data.data.length; i++) {
          imgName.push(result.data.data[i].files);
          fildernameurl.push(
            `http://192.168.29.3:8001/upload/${result.data.data[i].folder}/`
          );
        }
        setImgname(imgName);
        setfoldername(result.data.data[5].folder);
        setUrl(fildernameurl);

        // console.log(imgname);
        // console.log(result.data.data[0].files);
        // console.log(
        //   `http://192.168.29.3:8001/upload/${result.data.data[0].folder}/`
        // );
        // console.log(
        //   `http://192.168.29.3:8001/upload/${result.data.data[0].folder}/${result.data.data[0]}`
        // );
        // console.log(result.data.data);
      } else {
      }
    } catch (error) {}
  };
  // console.log(foldername);
  useEffect(() => {
    apicheck();
  }, []);

  return (
    <div id="Kamkala">
      <>
        <div
          className="md:mt-40 mt-20 mx-auto container p-5"
          // data-aos="fade-up"
          // data-aos-duration="2000"
        >
          <p className="font-Josefin_Sans font-bold xl:text-6xl md:text-5xl text-4xl  text-center text-[#374C9F]">
            {foldername}
          </p>

          <div className="font-Roboto gap-12   grid xl:grid-cols-4 lg:grid-cols-3 md:grid-cols-2 grid-cols-1">
            {imgname.map((items, key) => (
              <div className="hover:bg-opacity-50 rounded-lg  mt-16 ">
                <div
                  className="flex items-center justify-center  flex-col  transition ease-in-out delay-150  "
                  // data-aos="fade-up"
                  // data-aos-duration="3000"
                >
                  {/* {console.log(url + items.file_name)} */}
                  <img src={url + items.file_name} alt="Bonnie image" />
                </div>
              </div>
            ))}
          </div>
        </div>
      </>
    </div>
  );
}

export default Kamkala;
