import React, { useEffect } from "react";
import Museum from "../AboutUs/Museum/Museum";
import Blog from "../AboutUs/Blog/Blog";

function AboutUs() {
  useEffect(() => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  }, []);
  return (
    <>
      <Museum />
      <Blog />
    </>
  );
}

export default AboutUs;
